AS

BEGIN


BEGIN TRY
SET NOCOUNT ON

		SELECT CVE_ID,CVE_DESC,CWE_ID,UPDT_DT,BAS_SCOR,ACS_VCTR,ACS_CMPLX,CONFDTY_IMP,INGTY_IMP,AVL_IMP,VCTR_TXT,IMP_SUB_SCOR,EXPLT_SUB_SCOR
	    FROM CVE
		WHERE CVE_ID = @CVE_ID



END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
