CREATE PROCEDURE [dbo].[Analyst_INS_CLNT_VULN_INSTC]
(
       @ROW_STS_KEY               INTEGER,
       @ORG_KEY                          INTEGER,
       @CLNT_ENGMT_CD                    VARCHAR(30),  
       @SECUR_SRVC_CD                    VARCHAR(10),
       @VULN_SRC_KEY              INTEGER,
       @VULN_INSTC_STS_CD         VARCHAR(3),
       @VULN_SEV_CD               VARCHAR(3),
       @VULN_IMP_CD               VARCHAR(3),
       @RISK_PRBL_CD              VARCHAR(3),
       @RMDTN_CST_EFFRT_CD        VARCHAR(3) = NULL,
       @VULN_CATGY_CD		      VARCHAR(10),
       @CVE_ID                                  VARCHAR(25),
       @SRC_VULN_INSTC_ID         VARCHAR(150),
       @VULN_NM                          VARCHAR(255),
       @VULN_DESC                        TEXT,
       @IPADR                            VARCHAR(39),
       @PORT_NBR                         INTEGER,
       @SRC_ADVS_TXT              VARCHAR(1024),
       @VULN_BAS_SCOR                    DECIMAL(10,2),
       @VULN_IMP_SUB_SCOR         DECIMAL(10,2),
       @VULN_EXPLT_SUB_SCOR DECIMAL(10,2),
       @VULN_TMPRL_SCOR           DECIMAL(10,2),
       @VULN_ENV_SCOR                    DECIMAL(10,2),
       @VULN_VCTR_TXT                    VARCHAR(100),
       @NTWK_NM                          VARCHAR(150),
       @PRTCL_NM                         VARCHAR(255),
       @HST_NM                                  VARCHAR(150),
       @DOM_NM                                  VARCHAR(150),
       @SFTW_NM                          VARCHAR(150),
       @OS_KEY                                  INTEGER,
       @APPL_URL                         NVARCHAR(2000),
       @NETBIOS_NM                       VARCHAR(150),
       @MAC_ADR_NM                       VARCHAR(150),
       @VULN_TECH_COMMT_TXT TEXT,
       @VULN_IMP_COMMT_TXT        TEXT,
       @RECOM_COMMT_TXT           TEXT,
       @ROOT_CAUS_COMMT_TXT    TEXT,
       @USER_ID                          INTEGER,
       @VULN_OVALL_SCOR           DECIMAL(10,2),
       @OWASP_TOP_10_KEY          INTEGER,
       @schema                                  VARCHAR(50)

)
AS

CREATE PROCEDURE [dbo].[Analyst_INSClientVulnerabiltysecctrl]
(
       @CLNT_VULN_INSTC_KEY       INTEGER,
       @SECUR_CTL_CD              VARCHAR(20),
       @USER_ID                   INTEGER,
       @REG_CMPLN_CD              VARCHAR(20),
       @REG_CMPLN_VER             VARCHAR(20),
       @ROW_STS_KEY               INTEGER,
       @Flag                      VARCHAR(1),    
       @schema                    VARCHAR(50)       

)
AS

BEGIN


BEGIN TRY
SET NOCOUNT ON

			SELECT   SECUR_OBJ_NM,SECUR_CTL_NM,SECUR_CTL_DESC
			FROM	 SECUR_CTL			
			WHERE	 SECUR_CTL_FAM_CD	= @SECUR_CTL_FAM_CD
END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

CREATE PROCEDURE [dbo].[Analyst_ListCntrlByFamilyID]
(
	@SECUR_CTL_FAM_CD	VARCHAR(50)
)

AS

BEGIN


BEGIN TRY
SET NOCOUNT ON

			SELECT   RMDTN_CST_EFFRT_CD,RMDTN_CST_EFFRT_NM
			FROM	 RMDTN_CST_EFFRT		
			ORDER BY RMDTN_CST_EFFRT_ORDR_NBR
END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

BEGIN
DECLARE @Count INTEGER
BEGIN TRY SET NOCOUNT ON
BEGIN TRAN
IF @Flag = 'N' BEGIN SELECT COUNT(CVE_ID) TotalCount FROM CVE WHERE ISNULL(CVE_DESC,'') LIKE CASE WHEN @CVE_DESC = '' THEN ISNULL(CVE_DESC,'') ELSE '%' + @CVE_DESC +'%' END AND ISNULL(CVE_ID,'') LIKE CASE WHEN @CVE_ID = '' THEN ISNULL(CVE_ID,'') ELSE '%' + @CVE_ID +'%' END ;
SELECT CVE_ID ,CVE_DESC,PUBL_DT,LST_MOD_DT FROM CVE WHERE ISNULL(CVE_DESC,'') LIKE CASE WHEN @CVE_DESC = '' THEN ISNULL(CVE_DESC,'') ELSE '%' + @CVE_DESC +'%' END AND ISNULL(CVE_ID,'') LIKE CASE WHEN @CVE_ID = '' THEN ISNULL(CVE_ID,'') ELSE '%' + @CVE_ID +'%' END ORDER BY CVE_ID ASC OFFSET ((@PageNo - 1) * @RowspPage) ROWS FETCH NEXT @RowspPage ROWS ONLY; END
IF @Flag = 'A' BEGIN SELECT COUNT(CVE_ID) TotalCount FROM CVE WHERE ISNULL(CVE_DESC,'') LIKE CASE WHEN @CVE_DESC = '' THEN ISNULL(CVE_DESC,'') ELSE '%' + @CVE_DESC +'%' END AND ISNULL(CVE_ID,'') LIKE CASE WHEN @CVE_ID = '' THEN ISNULL(CVE_ID,'') ELSE '%' + @CVE_ID +'%' END ;
SELECT CVE_ID ,CVE_DESC,PUBL_DT,LST_MOD_DT FROM CVE WHERE ISNULL(CVE_DESC,'') LIKE CASE WHEN @CVE_DESC = '' THEN ISNULL(CVE_DESC,'') ELSE '%' + @CVE_DESC +'%' END AND ISNULL(CVE_ID,'') LIKE CASE WHEN @CVE_ID = '' THEN ISNULL(CVE_ID,'') ELSE '%' + @CVE_ID +'%' END ORDER BY PUBL_DT ASC OFFSET ((@PageNo - 1) * @RowspPage) ROWS FETCH NEXT @RowspPage ROWS ONLY; END
IF @Flag = 'D' BEGIN SELECT COUNT(CVE_ID) TotalCount FROM CVE WHERE ISNULL(CVE_DESC,'') LIKE CASE WHEN @CVE_DESC = '' THEN ISNULL(CVE_DESC,'') ELSE '%' + @CVE_DESC +'%' END AND ISNULL(CVE_ID,'') LIKE CASE WHEN @CVE_ID = '' THEN ISNULL(CVE_ID,'') ELSE '%' + @CVE_ID +'%' END ;
SELECT CVE_ID ,CVE_DESC,PUBL_DT,LST_MOD_DT FROM CVE WHERE ISNULL(CVE_DESC,'') LIKE CASE WHEN @CVE_DESC = '' THEN ISNULL(CVE_DESC,'') ELSE '%' + @CVE_DESC +'%' END AND ISNULL(CVE_ID,'') LIKE CASE WHEN @CVE_ID = '' THEN ISNULL(CVE_ID,'') ELSE '%' + @CVE_ID +'%' END ORDER BY PUBL_DT DESC OFFSET ((@PageNo - 1) * @RowspPage) ROWS FETCH NEXT @RowspPage ROWS ONLY; END
COMMIT TRAN END TRY
BEGIN CATCH
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH -- COMMIT TRANSACTION END

CREATE PROCEDURE [dbo].[Analyst_ListCve]
(
       
       @CVE_DESC                    VARCHAR(500),
       @PageNo                      INTEGER,
       @RowspPage					INTEGER,
       @CVE_ID                      VARCHAR(25),
       @Flag                        VARCHAR(1)
)
AS

BEGIN


BEGIN TRY
SET NOCOUNT ON

			SELECT  REG_CMPLN_CD, REG_CMPLN_VER,SECUR_CTL_FAM_CD,SECUR_CTL_FAM_NM,SECUR_OBJ_CD,SECUR_OBJ_NM,SECUR_CTL_NM,
			SECUR_CTL_DESC,SECUR_CTL_CD
			FROM	SECUR_CTL		
			WHERE	REG_CMPLN_CD= 'HITRUST'	

END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

BEGIN

BEGIN TRY
SET NOCOUNT ON

			SELECT  DISTINCT VULN_NM
			FROM	CLNT_VULN_INSTC				
			WHERE	ROW_STS_KEY			=   1
			AND		VULN_SRC_KEY		=   35

END TRY
BEGIN CATCH
    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

BEGIN


BEGIN TRY
SET NOCOUNT ON

			SELECT   [OS_KEY],[OS_NM]
			FROM	 OS		
			WHERE	 ISNULL(OS_NM,'')<>''
			ORDER BY [OS_KEY]
END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

