CREATE PROCEDURE [dbo].[Get_ClntRelEngmtIndenByID]
(
      @CLNT_ENGMT_CD	VARCHAR(30)
)
AS

BEGIN
BEGIN TRY
SET NOCOUNT ON

       SELECT		 A.CLNT_ENGMT_CD, A.SECUR_SRVC_CD, SRVC_ENGMT_STS_KEY, B.SECUR_SRVC_NM, A.ROW_STS_KEY, 
                     SRVC_EST_STRT_DT, SRVC_EST_END_DT, A.CREAT_DT, A.CREAT_USER_ID, A.UPDT_DT, A.UPDT_USER_ID,
                     COUNT(C.APPL_FL_UPLOAD_LOG_KEY) FileCount


       FROM          CLNT_SECUR_SRVC_ENGMT      A
       JOIN          SECUR_SRVC                 B
       ON            A.SECUR_SRVC_CD         =  B.SECUR_SRVC_CD
       LEFT JOIN     APPL_FL_UPLOAD_LOG         C
       ON            A.SECUR_SRVC_CD         =  C.SECUR_SRVC_CD
       AND           C.CLNT_ENGMT_CD         =  @CLNT_ENGMT_CD
       WHERE         A.CLNT_ENGMT_CD         =  @CLNT_ENGMT_CD
       GROUP BY      A.CLNT_ENGMT_CD, A.SECUR_SRVC_CD, SRVC_ENGMT_STS_KEY, B.SECUR_SRVC_NM, A.ROW_STS_KEY, 
                     SRVC_EST_STRT_DT, SRVC_EST_END_DT, A.CREAT_DT, A.CREAT_USER_ID, A.UPDT_DT, A.UPDT_USER_ID



END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END;

CREATE PROCEDURE [dbo].[Get_ClntSecurSrvcEngmtByID]
(
      @CLNT_ENGMT_CD	VARCHAR(30)
)
AS

BEGIN
BEGIN TRY
SET NOCOUNT ON


SELECT A.CLNT_ENGMT_CD,A.CLNT_ENGMT_NM,A.CLNT_ORG_KEY,B.ORG_NM,C.SECUR_PKG_CD,C.SECUR_PKG_NM,E.SECUR_SRVC_CD,E.SECUR_SRVC_NM,
	   D.SRVC_EST_STRT_DT,D.SRVC_EST_END_DT,A.AGR_DT,D.FL_LCK_IND
FROM   dbo.CLNT_ENGMT		A
JOIN   dbo.ORG				B
ON	   A.CLNT_ORG_KEY	 =  B.ORG_KEY
JOIN   dbo.SECUR_PKG		C
ON	   A.SECUR_PKG_CD	 =  C.SECUR_PKG_CD
JOIN   dbo.CLNT_SECUR_SRVC_ENGMT D
ON	   A.CLNT_ENGMT_CD	 =  D.CLNT_ENGMT_CD
JOIN   dbo.SECUR_SRVC		E
ON	   D.SECUR_SRVC_CD	 =  E.SECUR_SRVC_CD
WHERE  A.CLNT_ENGMT_CD	 =  @CLNT_ENGMT_CD
AND	   E.SECUR_SRVC_CD	 =  @SECUR_SRVC_CD


END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END;

CREATE PROCEDURE [dbo].[Get_EngageServiceByServiceID]
(
	@CLNT_ENGMT_CD	VARCHAR(30),
	@SECUR_SRVC_CD	VARCHAR(10),
	@USER_ID		INTEGER
	
)
AS


BEGIN  
 DECLARE @LiCOUNT INTEGER = 0;
BEGIN TRY  
SET NOCOUNT ON  

SELECT @LiCOUNT= COUNT(USER_ID)  FROM USER_PRFL WHERE ORG_KEY = @OrgID GROUP BY ORG_KEY

SELECT  A.ORG_KEY,A.ROW_STS_KEY,[dbo].[fnGetMasterLkpNameByID](A.ROW_STS_KEY) OrgStatus,  
		ISNULL(A.PAR_ORG_KEY,0) PAR_ORG_KEY,A.ORG_TYP_KEY,[dbo].[fnGetMasterLkpNameByID](A.ORG_TYP_KEY) [Org Type Name],B.LKP_ENTY_NM,A.ORG_INDUS_KEY,[dbo].[fnGetMasterLkpNameByID](A.ORG_INDUS_KEY) OrgIndustry,A.ORG_NM,A.STR_ADR_1,A.STR_ADR_2,A.CTY_NM,A.ST_NM,CNTRY_NM,  
		A.PST_CD,A.ORG_DESC,A.STS_COMMT_TXT ,ISNULL([dbo].[fnGetParentOrgNameID](A.PAR_ORG_KEY),'') PAR_ORG_NM,@LiCOUNT UserCount ,A.CREAT_ORG_SCHM_IND,A.ORG_SCHM
FROM    ORG      A  
JOIN	MSTR_LKP    B  
ON		A.ORG_TYP_KEY = B.MSTR_LKP_KEY  
WHERE   A.ORG_KEY   = CASE WHEN @OrgID = 0 THEN A.ORG_KEY ELSE @OrgID END 
AND		A.ORG_KEY <>1 
ORDER BY CASE WHEN ISNULL(A.UPDT_DT,'')='' THEN A.CREAT_DT ELSE A.UPDT_DT END DESC  


END TRY  

BEGIN CATCH  

    DECLARE @ErrorNumber INT = ERROR_NUMBER();  
    DECLARE @ErrorLine INT = ERROR_LINE();  
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();  
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();  
    DECLARE @ErrorState INT = ERROR_STATE();  

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));  
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));  

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);  
  END CATCH  
-- COMMIT TRANSACTION  
END

CREATE PROCEDURE [dbo].[Get_OrgDetailsByID]  
(  
 @OrgID INTEGER  
)  
AS


BEGIN  

BEGIN TRY  
SET NOCOUNT ON  




SELECT  A.ORG_KEY,A.ROW_STS_KEY,CASE WHEN A.ROW_STS_KEY=1 THEN 'Active' WHEN A.ROW_STS_KEY=2 THEN 'Inactive' END OrgStatus,  
  A.PAR_ORG_KEY,A.ORG_TYP_KEY,A.ORG_INDUS_KEY,A.ORG_NM,A.STR_ADR_1,A.STR_ADR_2,A.CTY_NM,A.ST_NM,CNTRY_NM,  
  A.PST_CD,A.ORG_DESC,C.ORG_REL_ID_KEY,C.SRC_KEY,B.LKP_ENTY_NM SourceName,C.SRC_CLNT_ID
FROM    ORG      A  
JOIN ORG_REL_ID    C  
ON  A.ORG_KEY  = C.ORG_KEY  
JOIN MSTR_LKP    B  
ON  C.SRC_KEY    = B.MSTR_LKP_KEY  
WHERE   A.ORG_KEY  = @OrgID  



END TRY  

BEGIN CATCH  

    DECLARE @ErrorNumber INT = ERROR_NUMBER();  
    DECLARE @ErrorLine INT = ERROR_LINE();  
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();  
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();  
    DECLARE @ErrorState INT = ERROR_STATE();  

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));  
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));  

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);  
  END CATCH  
-- COMMIT TRANSACTION  
END

--DROP PROCEDURE Get_OrgRelDetailsByID  
CREATE PROCEDURE [dbo].[Get_OrgRelDetailsByID]  
(  
 @OrgID INTEGER  
)  
AS

BEGIN BEGIN TRY SET NOCOUNT ON
IF @Flag = 'D' BEGIN SELECT A.ORG_KEY,ORG_NM,CLNT_ENGMT_CD,A.SECUR_SRVC_CD, A.UPLOAD_USER_ID,[dbo].[fnGetUserNameByID](A.UPLOAD_USER_ID) UploadedUser, [dbo].[fnGetUserEmailByID](A.UPLOAD_USER_ID) UploadedUserEmail, FL_NM,FL_FLDR_PTH,FL_COMMT,FL_UPLOAD_DT,A.APPL_FL_UPLOAD_LOG_KEY,A.FL_STS_KEY, A.FL_SZ
FROM APPL_FL_UPLOAD_LOG A JOIN ORG B ON A.ORG_KEY = B.ORG_KEY JOIN MSTR_LKP C ON C.MSTR_LKP_KEY = A.ROW_STS_KEY JOIN MSTR_LKP D ON D.MSTR_LKP_KEY = A.FL_STS_KEY --JOIN MSTR_LKP E --ON E.MSTR_LKP_KEY = A.DOC_TYP_KEY WHERE C.LKP_ENTY_NM = 'Active' AND D.LKP_ENTY_NM = 'Scan in Progress' --AND E.LKP_ENTY_NM = 'Data' AND CASE WHEN @PvcFlag = 'M' THEN DATEDIFF(MINUTE, FL_UPLOAD_DT, GETDATE()) WHEN @PvcFlag = 'H' THEN DATEDIFF(HOUR, FL_UPLOAD_DT, GETDATE()) END >= @PiHours ORDER BY A.FL_UPLOAD_DT DESC END
IF @Flag = 'R' BEGIN SELECT A.ORG_KEY,ORG_NM,CLNT_ENGMT_CD, A.CREAT_USER_ID,[dbo].[fnGetUserNameByID](A.CREAT_USER_ID) UploadedUser, [dbo].[fnGetUserEmailByID](A.CREAT_USER_ID) UploadedUserEmail, FL_NM,FL_FLDR_PTH,A.CREAT_DT,A.RPT_FL_UPLOAD_LOG_KEY,A.RPT_STS_KEY, A.FL_SZ,A.RPT_PUBL_DT
FROM RPT_FL_UPLOAD_LOG A JOIN ORG B ON A.ORG_KEY = B.ORG_KEY JOIN MSTR_LKP C ON C.MSTR_LKP_KEY = A.ROW_STS_KEY JOIN MSTR_LKP D ON D.MSTR_LKP_KEY = A.RPT_STS_KEY --JOIN MSTR_LKP E --ON E.MSTR_LKP_KEY = A.DOC_TYP_KEY WHERE C.LKP_ENTY_NM = 'Active' AND D.LKP_ENTY_NM = 'Scan in Progress' --AND E.LKP_ENTY_NM = 'Data' AND CASE WHEN @PvcFlag = 'M' THEN DATEDIFF(MINUTE, A.CREAT_DT, GETDATE()) WHEN @PvcFlag = 'H' THEN DATEDIFF(HOUR, A.CREAT_DT, GETDATE()) END >= @PiHours ORDER BY A.CREAT_DT DESC END
END TRY
BEGIN CATCH
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH -- COMMIT TRANSACTION END;

