CREATE PROCEDURE [dbo].[GetEntityIDByName]
(
	@EntityType		VARCHAR(30),
	@EntityName     VARCHAR(30)


		
)
AS

BEGIN
BEGIN TRY
SET NOCOUNT ON

SELECT	C.USER_ID [Engagment LeadID],(C.FST_NM + ' ' + C.LST_NM) [Engagement Lead Name],dbo.fnGetMasterLkpNameByID(C.USER_TYP_KEY) UserType,C.EMAIL_ID [Engagement Lead email]

FROM	CLNT_ENGMT_USER_ASGN		A
JOIN	USER_PRFL					C
ON		C.USER_ID		=	    A.USER_ID	
WHERE	A.CLNT_ENGMT_CD	=	@CLNT_ENGMT_CD


END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

BEGIN
BEGIN TRY
SET NOCOUNT ON

SELECT		TOP 1 ORG_KEY,CREAT_DT,CLNT_SCHM_LOG_KEY,ERR_DESC,SCHM_STS_DESC
FROM		CLNT_SCHM_LOG
WHERE		ORG_KEY = @ORG_KEY
ORDER BY	CLNT_SCHM_LOG_KEY DESC

END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

CREATE PROCEDURE [dbo].[GetOrgSchmaLogByID]
(
	@ORG_KEY	INTEGER

)
AS

BEGIN

BEGIN TRY
SET NOCOUNT ON


              SELECT        A.CLNT_ENGMT_CD,D.ORG_KEY,D.ORG_SCHM
              FROM			CLNT_ENGMT              A
			  JOIN			ORG						D
			  ON			A.CLNT_ORG_KEY		 =	D.ORG_KEY
              WHERE			A.CLNT_ENGMT_CD		 =  @CLNT_ENGMT_CD
              AND           A.ROW_STS_KEY		 =  1
			  AND			D.ROW_STS_KEY		 =  1


END TRY

BEGIN CATCH

    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END

CREATE PROCEDURE [dbo].[GetSchmaByEngmtCD]
(
       @CLNT_ENGMT_CD            VARCHAR(20)
       
)
AS


BEGIN  
BEGIN TRY  
SET NOCOUNT ON  


SELECT A.[USER_ID] ID,USER_TYP_KEY,dbo.fnGetMasterLkpNameByID(USER_TYP_KEY) [User Type],B.ORG_NM [Organization Name],
	   A.ORG_KEY,FST_NM [First Name],MIDL_NM [Middle Name],LST_NM [Last Name],JOB_TITL_NM [Job Title],EMAIL_ID [EMAIL ID],
	   TEL_NBR [Phone Number],dbo.fnGetMasterLkpNameByID(A.ROW_STS_KEY)[Status], A.STS_COMMT_TXT,A.ROW_STS_KEY	

FROM   USER_PRFL			 A
JOIN   ORG					 B
ON	   A.ORG_KEY		   = B.ORG_KEY
WHERE  A.[USER_ID]         = @UserID 
ORDER BY CASE WHEN ISNULL(A.UPDT_DT,'')='' THEN A.CREAT_DT ELSE A.UPDT_DT END DESC  
END TRY  

BEGIN CATCH  


    DECLARE @ErrorNumber INT = ERROR_NUMBER();  
    DECLARE @ErrorLine INT = ERROR_LINE();  
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();  
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();  
    DECLARE @ErrorState INT = ERROR_STATE();  

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));  
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));  

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);  
  END CATCH  
-- COMMIT TRANSACTION  
END

CREATE PROCEDURE [dbo].[GetUserDetailsByID]  
(  
       @UserID  INTEGER 
)  
AS

BEGIN
BEGIN TRY
SET NOCOUNT ON

SELECT A.USER_ID,A.ORG_KEY,A.USER_TYP_KEY,[dbo].[fnGetMasterLkpNameByID](A.USER_TYP_KEY) USER_TYPE,
	   A.ROW_STS_KEY,A.FST_NM,A.LST_NM,A.MIDL_NM,A.JOB_TITL_NM,A.EMAIL_ID,A.TEL_NBR,A.LCK_IND,
	   A.LOGIN_ATMPT_CNT,A.LST_LOGIN_DT,A.PSWD_RSET_DT,A.CREAT_DT,A.USER_VERF_IND,MAC_ADR_NM,
	   B.ROW_STS_KEY ORG_STS_KEY,B.ORG_NM,B.ORG_SCHM 
FROM   USER_PRFL	  A
JOIN    ORG           B
ON      A.ORG_KEY   = B.ORG_KEY 
WHERE  A.EMAIL_ID   = @UserID
END TRY

BEGIN CATCH


    DECLARE @ErrorNumber INT = ERROR_NUMBER();
    DECLARE @ErrorLine INT = ERROR_LINE();
    DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
    DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
    DECLARE @ErrorState INT = ERROR_STATE();

    PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
    PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

    RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
  END CATCH
-- COMMIT TRANSACTION
END;

CREATE PROCEDURE [dbo].[GetUserDetailsByName]
(
       @UserID  VARCHAR(150)
)
AS

BEGIN BEGIN TRY SET NOCOUNT ON
SELECT A.PERMSN_KEY,A.PERMSN_NM,A.PERMSN_DESC,A.DSPL_TXT,C.CHLD_XST_IND,A.SEQ_ORDR, D.PERMSN_KEY ModuleID,D.PERMSN_NM ModuleName,C.PERMSN_KEY MenuID,C.PERMSN_NM MenuName, B.PERMSN_KEY SubmenuID,B.PERMSN_NM SubmenuName FROM PERMSN A JOIN PERMSN_GRP_ASSOC E ON A.PERMSN_KEY = E.PERMSN_KEY JOIN PERMSN B ON E.SUB_MNU_ID = B.PERMSN_KEY AND B.PERMSN_TYP_KEY = 6 JOIN PERMSN C ON E.MNU_ID = C.PERMSN_KEY AND C.PERMSN_TYP_KEY = 5 JOIN PERMSN D ON E.MDUL_ID = D.PERMSN_KEY AND D.PERMSN_TYP_KEY = 4 JOIN APPL_ROLE_PERMSN_GRP F ON E.PERMSN_GRP_KEY = F.PERMSN_GRP_KEY AND F.ROW_STS_KEY = 1 JOIN USER_APPL_ROLE G ON F.APPL_ROLE_KEY = G.APPL_ROLE_KEY AND G.ROW_STS_KEY = 1 JOIN APPL_ROLE H ON H.APPL_ROLE_KEY = G.APPL_ROLE_KEY AND G.ROW_STS_KEY = 1 AND H.ROW_STS_KEY = 1 JOIN PERMSN_GRP I ON E.PERMSN_GRP_KEY = I.PERMSN_GRP_KEY AND I.ROW_STS_KEY = 1 WHERE A.ROW_STS_KEY = 1 AND E.ROW_STS_KEY = 1 AND G.USER_ID = @UserKey ORDER BY C.PERMSN_KEY,B.PERMSN_KEY,A.SEQ_ORDR
END TRY
BEGIN CATCH IF @@TRANCOUNT > 0 ROLLBACK TRANSACTION;
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH -- COMMIT TRANSACTION END

