 CREATE PROCEDURE [dbo].[INS_EngagementAssignUsers](  
 @CLNT_ENGMT_CD          VARCHAR(30),  
 @USER_ID                INTEGER,  
 @APPL_ROLE_KEY          INTEGER,  
 @CREAT_USER_ID          INTEGER
   
 )  
 AS

BEGIN SET NOCOUNT ON BEGIN TRY
IF (ISNULL(@JOB_STRT_DT,'') = '') SELECT @JOB_STRT_DT = GETDATE();
INSERT INTO [dbo].[ETL_AUD_LOG] ([JOB_NM] ,[JOB_STRT_DT] ,[JOB_END_DT] ,[JOB_STS] ,[SRC_FL_NM] ,[SRC_FL_FLDR_PTH] ,[TGT_TBL_NM] ,[SRC_ROW_CNT] ,[TGT_INSRT_ROW_CNT] ,[TGT_UPDT_ROW_CNT] ,[TGT_REJ_ROW_CNT]) VALUES ( @JOB_NM ,@JOB_STRT_DT ,@JOB_END_DT ,@JOB_STS ,@SRC_FL_NM ,@SRC_FL_FLDR_PTH ,@TGT_TBL_NM ,@SRC_ROW_CNT ,@TGT_INSRT_ROW_CNT ,@TGT_UPDT_ROW_CNT ,@TGT_REJ_ROW_CNT)
SELECT @RETVAL = SCOPE_IDENTITY() RETURN;
END TRY
BEGIN CATCH
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH
END

