*/
CREATE PROCEDURE [dbo].[INS_ETL_AUD_LOG] ( @JOB_NM VARCHAR(150) ,@JOB_STRT_DT DATETIME ,@JOB_END_DT DATETIME ,@JOB_STS VARCHAR(100) ,@SRC_FL_NM VARCHAR(150) ,@SRC_FL_FLDR_PTH VARCHAR(500) ,@TGT_TBL_NM VARCHAR(50) ,@SRC_ROW_CNT INT ,@TGT_INSRT_ROW_CNT INT ,@TGT_UPDT_ROW_CNT INT ,@TGT_REJ_ROW_CNT INT ,@RETVAL INT OUTPUT ) AS

BEGIN
	SET NOCOUNT ON
	BEGIN TRY

		IF (ISNULL(@ERR_LOG_DT,'') = '') SELECT @ERR_LOG_DT = GETDATE();

		INSERT INTO [dbo].[ETL_ERR_LOG]
           ([ETL_AUD_LOG_KEY]
           ,[ERR_LOG_DT]
           ,[ERR_CD]
           ,[ERR_MSG_TXT]
           ,[ERR_REC_TXT])
		 VALUES
			(
			@ETL_AUD_LOG_KEY
			,@ERR_LOG_DT
			,@ERR_CD
			,@ERR_MSG_TXT
			,@ERR_REC_TXT
			)

		 SELECT SCOPE_IDENTITY() AS RETVAL

	END TRY

	BEGIN CATCH

		DECLARE @ErrorNumber INT = ERROR_NUMBER();
		DECLARE @ErrorLine INT = ERROR_LINE();
		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
		PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH

END

