       SELECT dbo.fnGetParentOrgNameID(B.CLNT_ORG_KEY) [Client NAME],B.CLNT_ORG_KEY, A.CLNT_ENGMT_CD,B.CLNT_ENGMT_NM,D.SECUR_SRVC_NM,D.SECUR_SRVC_CD,
       A.FL_LCK_IND,G.USER_STRT_DT,G.USER_END_DT,G.USER_ID
