/* DECLARE @SRC_COUNTS INT = 0 ,@INSERT_COUNTS INT = 0 ,@UPDATE_COUNTS INT = 0 ,@DELETE_COUNTS INT = 0; EXEC [dbo].[MERGE_CLNT_VULN_INSTC_TRIPWIRE_IP360_MT] 'JOH' ,'[JOH].[TRIPWIRE_IP360_STG]' ,'[JOH].[CLNT_VULN_INSTC]' , @SRC_COUNTS OUTPUT , @INSERT_COUNTS OUTPUT , @UPDATE_COUNTS OUTPUT , @DELETE_COUNTS OUTPUT
SELECT @SRC_COUNTS SELECT @INSERT_COUNTS SELECT @UPDATE_COUNTS SELECT @DELETE_COUNTS
--## - PLEASE VERIFY */
CREATE PROCEDURE [dbo].[MERGE_CLNT_VULN_INSTC_TRIPWIRE_IP360_MT] @SCHEMA_NM VARCHAR(1000), @STG_TBL_NAME VARCHAR(1000), @MASTER_TBL_NAME VARCHAR(1000), @SRC_COUNTS INT OUTPUT, @INSERT_COUNTS INT OUTPUT, @UPDATE_COUNTS INT OUTPUT, @DELETE_COUNTS INT OUTPUT AS

BEGIN
IF OBJECT_ID('tempdb..#CLNT_VULN_SECUR_CTL') IS NOT NULL DROP TABLE #CLNT_VULN_SECUR_CTL;
CREATE TABLE #CLNT_VULN_SECUR_CTL( [CLNT_VULN_INSTC_KEY] [int] NOT NULL, [REG_CMPLN_CD] [varchar](20) NOT NULL, [REG_CMPLN_VER] [varchar](20) NOT NULL, [SECUR_CTL_CD] [varchar](20) NOT NULL )
IF OBJECT_ID('tempdb..#SecurCtlData') IS NOT NULL DROP TABLE #SecurCtlData;
CREATE TABLE #SecurCtlData( [REG_CMPLN_CD] [varchar](20) NOT NULL, [REG_CMPLN_VER] [varchar](20) NOT NULL, [SECUR_CTL_CD] [varchar](20) NOT NULL, [PRI_REG_CMPLN_CD] [varchar](20) NOT NULL, [PRI_REG_CMPLN_VER] [varchar](20) NOT NULL, [PRI_SECUR_CTL_CD] [varchar](20) NOT NULL )
IF OBJECT_ID('tempdb..#OP_SummaryOfChanges') IS NOT NULL DROP TABLE #OP_SummaryOfChanges;
CREATE TABLE #OP_SummaryOfChanges( [INSERT_COUNTS] [int] NULL, [UPDATE_COUNTS] [int] NULL, [DELETE_COUNTS] [int] NULL, );
-- #Populate Primary and Secondary control mapping SELECT DISTINCT PRI_SECUR_CTL_CD, PRI_REG_CMPLN_CD, PRI_REG_CMPLN_VER INTO #secur_ctl_CTE FROM [dbo].SECUR_CTL_MAP
INSERT #SecurCtlData([REG_CMPLN_CD], [REG_CMPLN_VER], [SECUR_CTL_CD], [PRI_REG_CMPLN_CD], [PRI_REG_CMPLN_VER], [PRI_SECUR_CTL_CD] ) SELECT [REG_CMPLN_CD], [REG_CMPLN_VER], [SECUR_CTL_CD], [PRI_REG_CMPLN_CD], [PRI_REG_CMPLN_VER], [PRI_SECUR_CTL_CD] FROM ( SELECT DISTINCT PRI_REG_CMPLN_CD AS REG_CMPLN_CD, PRI_REG_CMPLN_VER AS REG_CMPLN_VER, PRI_SECUR_CTL_CD AS SECUR_CTL_CD , [PRI_REG_CMPLN_CD], [PRI_REG_CMPLN_VER], [PRI_SECUR_CTL_CD] FROM #secur_ctl_CTE UNION SELECT SEC_REG_CMPLN_CD AS REG_CMPLN_CD, SEC_REG_CMPLN_VER AS REG_CMPLN_VER, SEC_SECUR_CTL_CD AS SECUR_CTL_CD , secur_ctl_map.PRI_REG_CMPLN_CD, secur_ctl_map.PRI_REG_CMPLN_VER, secur_ctl_map.PRI_SECUR_CTL_CD FROM [dbo].SECUR_CTL_MAP secur_ctl_map JOIN #secur_ctl_CTE ON secur_ctl_map.PRI_REG_CMPLN_CD = #secur_ctl_CTE.PRI_REG_CMPLN_CD AND secur_ctl_map.PRI_REG_CMPLN_VER = #secur_ctl_CTE.PRI_REG_CMPLN_VER AND secur_ctl_map.PRI_SECUR_CTL_CD = #secur_ctl_CTE.PRI_SECUR_CTL_CD ) SecurCtlTemp;
-- SET XACT_ABORT ON will cause the transaction to be uncommittable when the constraint violation occurs. SET XACT_ABORT ON;
BEGIN TRY SET NOCOUNT ON
--DECLARE @SCHEMA_NM VARCHAR(1000), @STG_TBL_NAME VARCHAR(1000), @MASTER_TBL_NAME VARCHAR(1000), @MAPPING_TBL_NAME VARCHAR(1000);; --SET @SCHEMA_NM = 'JOH'; --SET @MASTER_TBL_NAME = '[JOH].[CLNT_VULN_INSTC]'; --SET @MAPPING_TBL_NAME = '[JOH].[CLNT_VULN_SECUR_CTL]';
DECLARE @SQL_TEMP VARCHAR(MAX), @SQL_MERGE VARCHAR(MAX);
--BEGIN TRANSACTION MERGE_CLNT_VULN_SECUR_CTL WITH MARK N'MERGE_CLNT_VULN_SECUR_CTL to process the Vulnerability data.'
SET @SQL_TEMP = '
INSERT #CLNT_VULN_SECUR_CTL SELECT DISTINCT _clientvuln.CLNT_VULN_INSTC_KEY ,SecurCtldata.[REG_CMPLN_CD] ,SecurCtldata.[REG_CMPLN_VER] ,SecurCtldata.[SECUR_CTL_CD] FROM ' + @MASTER_TBL_NAME + ' _clientvuln WITH(NOLOCK) INNER JOIN [dbo].[CVE] _cve WITH(NOLOCK) ON _clientvuln.[CVE_ID] = _cve.[CVE_ID] INNER JOIN [dbo].[CVE_SECUR_CTL_MAP] _cvescrtymap WITH(NOLOCK) ON _cvescrtymap.[CVE_ID] = _cve.[CVE_ID] AND _cvescrtymap.ROW_STS_KEY IN (SELECT MSTR_LKP_KEY FROM dbo.MSTR_LKP WHERE LKP_ENTY_TYP_NM = ''Row Status'' and LKP_ENTY_NM = ''Active'') INNER JOIN #SecurCtldata SecurCtldata on _cvescrtymap.REG_CMPLN_CD = SecurCtldata.PRI_REG_CMPLN_CD and _cvescrtymap.REG_CMPLN_VER = SecurCtldata.PRI_REG_CMPLN_VER and _cvescrtymap.SECUR_CTL_CD = SecurCtldata.PRI_SECUR_CTL_CD WHERE NOT EXISTS ( SELECT TOP 1 _clientvulnsecurctl.CLNT_VULN_INSTC_KEY FROM ' + @MAPPING_TBL_NAME + ' _clientvulnsecurctl WITH(NOLOCK) WHERE _clientvuln.CLNT_VULN_INSTC_KEY = _clientvulnsecurctl.CLNT_VULN_INSTC_KEY );'
PRINT @SQL_TEMP; EXEC (@SQL_TEMP);
SELECT @SRC_COUNTS = COUNT(*) FROM #CLNT_VULN_SECUR_CTL PRINT N'Message: TOTAL ' + CAST(@SRC_COUNTS AS VARCHAR) + N' RECORDS FROM SOURCE.'
SET @SQL_MERGE = '
DECLARE @SummaryOfChanges table (Change VARCHAR(20));
;WITH _target AS ( SELECT * FROM ' + @MAPPING_TBL_NAME + ' ) , _source AS ( SELECT * FROM #CLNT_VULN_SECUR_CTL )
MERGE INTO _target USING _source ON ( _target.CLNT_VULN_INSTC_KEY=_source.CLNT_VULN_INSTC_KEY AND _target.REG_CMPLN_CD=_source.REG_CMPLN_CD AND _target.REG_CMPLN_VER=_source.REG_CMPLN_VER AND _target.SECUR_CTL_CD=_source.SECUR_CTL_CD ) WHEN NOT MATCHED THEN INSERT ( [CLNT_VULN_INSTC_KEY] ,[REG_CMPLN_CD] ,[REG_CMPLN_VER] ,[SECUR_CTL_CD] ,[ROW_STS_KEY] ,[CREAT_DT] ,[CREAT_USER_ID] ,[UPDT_DT] ,[UPDT_USER_ID] ) VALUES ( _source.CLNT_VULN_INSTC_KEY ,_source.REG_CMPLN_CD ,_source.REG_CMPLN_VER ,_source.SECUR_CTL_CD ,(SELECT MSTR_LKP_KEY FROM dbo.MSTR_LKP WHERE LKP_ENTY_TYP_NM = ''Row Status'' and LKP_ENTY_NM = ''Active'') ,GETDATE() ,''2'' ,NULL ,NULL )
OUTPUT $ACTION INTO @SummaryOfChanges OPTION(RECOMPILE);
INSERT INTO #OP_SummaryOfChanges VALUES( (SELECT COUNT(*) AS INSERT_COUNTS FROM @SummaryOfChanges WHERE Change = ''INSERT'') ,(SELECT COUNT(*) AS UPDATE_COUNTS FROM @SummaryOfChanges WHERE Change = ''UPDATE'') ,(SELECT COUNT(*) AS DELETE_COUNTS FROM @SummaryOfChanges WHERE Change = ''DELETE'') );
'
--PRINT @SQL_MERGE; EXEC (@SQL_MERGE);
SELECT @INSERT_COUNTS = INSERT_COUNTS FROM #OP_SummaryOfChanges PRINT N'Message: Total ' + CAST(@INSERT_COUNTS AS VARCHAR) + N' VULN INSERTED.'
SELECT @UPDATE_COUNTS = UPDATE_COUNTS FROM #OP_SummaryOfChanges PRINT N'Message: TOTAL ' + CAST(@UPDATE_COUNTS AS VARCHAR) + N' VULN UPDATED.'
SELECT @DELETE_COUNTS = DELETE_COUNTS FROM #OP_SummaryOfChanges PRINT N'Message: Total ' + CAST(@DELETE_COUNTS AS VARCHAR) + N' VULN INSERTED.'
IF (XACT_STATE()) = 1 BEGIN PRINT N'The transaction is committable. Committing transaction.' COMMIT TRANSACTION MERGE_CLNT_VULN_SECUR_CTL; END;
END TRY
BEGIN CATCH
IF (XACT_STATE()) = -1 BEGIN PRINT N'The transaction is in an uncommittable state. Rolling back transaction.' ROLLBACK TRANSACTION MERGE_CLNT_VULN_SECUR_CTL END;
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH
END

/****************************** ** Desc: ** Auth: GM ** Date: 06/20/2016 ************************** ** Change History ************************** ** Date Author Description ** -------- -- ------------------------------------ ** 11/29/2016 GM Multi-tenancy changes **************************************/
/*
DECLARE @SRC_COUNTS INT = 0 ,@INSERT_COUNTS INT = 0 ,@UPDATE_COUNTS INT = 0 ,@DELETE_COUNTS INT = 0; EXEC [dbo].[MERGE_CLNT_VULN_SECUR_CTL_MT] 'JOH' ,'[JOH].[CLNT_VULN_INSTC]' ,'[JOH].[CLNT_VULN_SECUR_CTL]' , @SRC_COUNTS OUTPUT , @INSERT_COUNTS OUTPUT , @UPDATE_COUNTS OUTPUT , @DELETE_COUNTS OUTPUT
SELECT @SRC_COUNTS SELECT @INSERT_COUNTS SELECT @UPDATE_COUNTS SELECT @DELETE_COUNTS
--## - PLEASE VERIFY */
CREATE PROCEDURE [dbo].[MERGE_CLNT_VULN_SECUR_CTL_MT] @SCHEMA_NM VARCHAR(1000), @MASTER_TBL_NAME VARCHAR(1000), @MAPPING_TBL_NAME VARCHAR(1000), @SRC_COUNTS INT OUTPUT, @INSERT_COUNTS INT OUTPUT, @UPDATE_COUNTS INT OUTPUT, @DELETE_COUNTS INT OUTPUT AS

BEGIN
declare @mergeResultsTable table (MergeAction VARCHAR(20));
BEGIN TRY SET NOCOUNT ON
MERGE DBO.CVE AS T USING ( SELECT CVE_ID, PUBL_DT, LST_MOD_DT, CVE_DESC, metric.BAS_SCOR, metric.ACS_VCTR, metric.ACS_CMPLX, metric.AUTH, metric.CONFDTY_IMP, metric.INGTY_IMP, metric.AVL_IMP, ltrim(CWE.CWE_LIST_ID) AS CWE_ID, '(' + Acc_Vec.MTRC_VCTR_CD + ':' + Acc_Vec.MTRC_VAL_VCTR_CD + '/' + Acc_cmp.MTRC_VCTR_CD + ':' + Acc_cmp.MTRC_VAL_VCTR_CD + '/' + Authen.MTRC_VCTR_CD + ':' + Authen.MTRC_VAL_VCTR_CD + '/' + Conf_Imp.MTRC_VCTR_CD + ':' + Conf_Imp.MTRC_VAL_VCTR_CD + '/' + Intg_Impc.MTRC_VCTR_CD + ':' + Intg_Impc.MTRC_VAL_VCTR_CD + '/' + Avail_Imp.MTRC_VCTR_CD + ':' + Avail_Imp.MTRC_VAL_VCTR_CD + ')' VCTR_TXT, cast ( cast(10.41*(1-(1-Conf_Imp.MTRC_VAL_SCOR)*(1-Intg_Impc.MTRC_VAL_SCOR)*(1-Avail_Imp.MTRC_VAL_SCOR)) as decimal(10,1)) as float ) AS IMP_SUB_SCOR, cast( cast(20 * Acc_Vec.MTRC_VAL_SCOR * Acc_cmp.MTRC_VAL_SCOR * Authen.MTRC_VAL_SCOR as decimal(10,1)) as float ) as EXPLT_SUB_SCOR FROM stg.CVE_ENTRY_STG ENT LEFT OUTER JOIN ( SELECT ENTRY_ID ,STUFF((SELECT ', ' + CAST(CWE_ID AS VARCHAR(10)) [text()] FROM stg.CVE_CWE_STG WHERE ENTRY_ID = t.ENTRY_ID FOR XML PATH(''), TYPE).value('.','VARCHAR(50)'),1,2,' ') CWE_LIST_ID FROM stg.CVE_CWE_STG t GROUP BY ENTRY_ID ) CWE ON ENT.ENTRY_ID = CWE.ENTRY_ID LEFT OUTER JOIN stg.CVE_CVSS_STG cvss on ENT.ENTRY_ID = cvss.ENTRY_ID LEFT OUTER JOIN stg.CVE_BAS_METR_STG metric on cvss.cvss_id = metric.cvss_id LEFT OUTER JOIN ( SELECT MTRC_VCTR_CD, MTRC_VAL_VCTR_CD, MTRC_VAL_TXT, MTRC_VAL_SCOR FROM dbo.CVSS_SCOR_MTRC where mtrc_nm = 'Access Vector' ) Acc_Vec ON metric.ACS_VCTR = Acc_Vec.MTRC_VAL_TXT LEFT OUTER JOIN ( SELECT MTRC_VCTR_CD, MTRC_VAL_VCTR_CD, MTRC_VAL_TXT, MTRC_VAL_SCOR FROM dbo.CVSS_SCOR_MTRC where mtrc_nm = 'Access Complexity' ) Acc_cmp ON metric.ACS_CMPLX = Acc_cmp.MTRC_VAL_TXT LEFT OUTER JOIN ( SELECT MTRC_VCTR_CD, MTRC_VAL_VCTR_CD, MTRC_VAL_TXT, MTRC_VAL_SCOR FROM dbo.CVSS_SCOR_MTRC where mtrc_nm = 'Authentication' ) Authen ON metric.AUTH = Authen.MTRC_VAL_TXT LEFT OUTER JOIN ( SELECT MTRC_VCTR_CD, MTRC_VAL_VCTR_CD, MTRC_VAL_TXT, MTRC_VAL_SCOR FROM dbo.CVSS_SCOR_MTRC where mtrc_nm = 'Confidentiality Impact' ) Conf_Imp ON metric.CONFDTY_IMP = Conf_Imp.MTRC_VAL_TXT LEFT OUTER JOIN ( SELECT MTRC_VCTR_CD, MTRC_VAL_VCTR_CD, MTRC_VAL_TXT, MTRC_VAL_SCOR FROM dbo.CVSS_SCOR_MTRC where mtrc_nm = 'Integrity Impact' ) Intg_Impc ON metric.INGTY_IMP = Intg_Impc.MTRC_VAL_TXT LEFT OUTER JOIN ( SELECT MTRC_VCTR_CD, MTRC_VAL_VCTR_CD, MTRC_VAL_TXT, MTRC_VAL_SCOR FROM dbo.CVSS_SCOR_MTRC where mtrc_nm = 'Availability Impact' ) Avail_Imp ON metric.AVL_IMP = Avail_Imp.MTRC_VAL_TXT
) AS S ON S.CVE_ID = T.CVE_ID WHEN NOT MATCHED BY TARGET THEN INSERT( CVE_ID, CVE_DESC, PUBL_DT, LST_MOD_DT, CWE_ID, BAS_SCOR, ACS_VCTR, ACS_CMPLX, AUTH, CONFDTY_IMP, INGTY_IMP, AVL_IMP, VCTR_TXT, IMP_SUB_SCOR, EXPLT_SUB_SCOR, CREAT_DT, CREAT_USER_ID) VALUES( S.CVE_ID, S.CVE_DESC, S.PUBL_DT, S.LST_MOD_DT, S.CWE_ID, S.BAS_SCOR, S.ACS_VCTR, S.ACS_CMPLX, S.AUTH, S.CONFDTY_IMP, S.INGTY_IMP, S.AVL_IMP, S.VCTR_TXT, S.IMP_SUB_SCOR, S.EXPLT_SUB_SCOR, GETDATE(), 2) WHEN MATCHED THEN UPDATE SET T.CVE_DESC = S.CVE_DESC, T.PUBL_DT = S.PUBL_DT, T.LST_MOD_DT = S.LST_MOD_DT, T.CWE_ID = S.CWE_ID, T.BAS_SCOR = S.BAS_SCOR, T.ACS_VCTR = S.ACS_VCTR, T.ACS_CMPLX = S.ACS_CMPLX, T.AUTH = S.AUTH, T.CONFDTY_IMP = S.CONFDTY_IMP, T.INGTY_IMP = S.INGTY_IMP, T.AVL_IMP = S.AVL_IMP, T.VCTR_TXT = S.VCTR_TXT, T.IMP_SUB_SCOR = S.IMP_SUB_SCOR, T.EXPLT_SUB_SCOR = S.EXPLT_SUB_SCOR, T.UPDT_DT = GETDATE(), UPDT_USER_ID = 2
output $action into @mergeResultsTable;
select @ins_row_cnt = [INSERT], @upd_row_cnt = [UPDATE] from (select 'NOOP' MergeAction -- row for null merge into null union all select * from @mergeResultsTable) mergeResultsPlusEmptyRow pivot (count(MergeAction) for MergeAction in ([INSERT],[UPDATE],[DELETE])) as mergeResultsPivot;
END TRY
BEGIN CATCH
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
END CATCH
END

/******************************************************************** ** Desc: To load CVE table ** Auth: Prakash Selvaraj ** Date: 06/20/2016 ************************** ** Change History ************************** ** Date Author Description ** 06/20/2016 Prakash Selvaraj Inital Version *********************************************************************/
/* EXEC [dbo].[MERGE_CVE]
*/
CREATE PROCEDURE [dbo].[MERGE_CVE] @ins_row_cnt int output, @upd_row_cnt int output AS

BEGIN
declare @mergeResultsTable table (MergeAction VARCHAR(20));
BEGIN TRY SET NOCOUNT ON
MERGE DBO.CVE_SECUR_CTL_MAP AS T USING (
SELECT a.CVE_ID, a.REG_CMPLN_CD, a.REG_CMPLN_VER, LTRIM(Split.a.value('.', 'VARCHAR(100)')) AS SECUR_CTL_CD_SPLIT FROM ( SELECT CVE_ID, REG_CMPLN_CD, REG_CMPLN_VER, CAST ('<M>' + REPLACE(SECUR_CTL_CD, ',', '</M><M>') + '</M>' AS XML) AS String FROM stg.CVE_SECUR_CTL_MAP_STG
) AS a CROSS APPLY String.nodes ('/M') AS Split(a)
join dbo.cve cve on a.cve_id = cve.cve_id join dbo.secur_ctl sec on a.REG_CMPLN_CD = sec.REG_CMPLN_CD and a.REG_CMPLN_VER = sec.REG_CMPLN_VER and LTRIM(Split.a.value('.', 'VARCHAR(100)')) = sec.SECUR_CTL_CD
) AS S ON S.CVE_ID = T.CVE_ID AND S.REG_CMPLN_CD = T.REG_CMPLN_CD AND S.REG_CMPLN_VER = T.REG_CMPLN_VER AND S.SECUR_CTL_CD_SPLIT = T.SECUR_CTL_CD
WHEN NOT MATCHED BY TARGET
THEN INSERT ( CVE_ID, REG_CMPLN_CD, REG_CMPLN_VER, SECUR_CTL_CD, ROW_STS_KEY, CREAT_DT, CREAT_USER_ID) VALUES
( S.CVE_ID, S.REG_CMPLN_CD, S.REG_CMPLN_VER, S.SECUR_CTL_CD_SPLIT, 1, GETDATE(), 2)
WHEN MATCHED THEN UPDATE SET T.CVE_ID = S.CVE_ID, T.REG_CMPLN_CD = S.REG_CMPLN_CD, T.REG_CMPLN_VER = S.REG_CMPLN_VER, T.SECUR_CTL_CD = S.SECUR_CTL_CD_SPLIT, T.ROW_STS_KEY = 1, T.UPDT_DT = GETDATE(), T.UPDT_USER_ID = 2
output $action into @mergeResultsTable;
select @ins_row_cnt = [INSERT], @upd_row_cnt = [UPDATE] from (select 'NOOP' MergeAction -- row for null merge into null union all select * from @mergeResultsTable) mergeResultsPlusEmptyRow pivot (count(MergeAction) for MergeAction in ([INSERT],[UPDATE],[DELETE])) as mergeResultsPivot;
-- Marking Inactive records
UPDATE TGT SET ROW_STS_KEY = 2, UPDT_DT = GETDATE(), UPDT_USER_ID = 2 FROM dbo.CVE_SECUR_CTL_MAP TGT JOIN ( SELECT DISTINCT CVE_ID FROM STG.CVE_SECUR_CTL_MAP_STG ) STG ON TGT.CVE_ID = STG.CVE_ID LEFT OUTER JOIN ( SELECT a.CVE_ID, a.REG_CMPLN_CD, a.REG_CMPLN_VER, LTRIM(Split.a.value('.', 'VARCHAR(100)')) AS SECUR_CTL_CD_SPLIT FROM (SELECT CVE_ID, REG_CMPLN_CD, REG_CMPLN_VER, CAST ('<M>' + REPLACE(SECUR_CTL_CD, ',', '</M><M>') + '</M>' AS XML) AS String FROM stg.CVE_SECUR_CTL_MAP_STG ) AS A CROSS APPLY String.nodes ('/M') AS Split(a) ) STG1
ON TGT.CVE_ID = STG1.CVE_ID AND TGT.REG_CMPLN_CD = STG1.REG_CMPLN_CD AND TGT.REG_CMPLN_VER = STG1.REG_CMPLN_VER AND TGT.SECUR_CTL_CD = STG1.SECUR_CTL_CD_SPLIT WHERE STG1.CVE_ID IS NULL AND STG1.REG_CMPLN_CD IS NULL AND STG1.REG_CMPLN_VER IS NULL AND STG1.SECUR_CTL_CD_SPLIT IS NULL;
END TRY
BEGIN CATCH
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
END CATCH
END

/******************************************************************** ** Desc: To load CVE_SECUR_CTL_MAP table ** Auth: Prakash Selvaraj ** Date: 06/20/2016 ************************** ** Change History ************************** ** Date Author Description ** 06/20/2016 Prakash Selvaraj Inital Version ** 07/11/2016 Prakash Selvaraj Added Try-Catch to capture the errors *********************************************************************/
/* EXEC [dbo].[MERGE_CVE_SECUR_CTL_MAP]
*/
CREATE PROCEDURE [dbo].[MERGE_CVE_SECUR_CTL_MAP] @ins_row_cnt int output, @upd_row_cnt int output AS

BEGIN
SET NOCOUNT ON;
DECLARE @ROWS INT = 0 ,@TARGETROWS INT = 0 ,@MESSAGE NVARCHAR(4000) ,@DEBUG INT = 0 --,@SRC_COUNTS INT = 0 --,@INSERT_COUNTS INT = 0 --,@UPDATE_COUNTS INT = 0 --,@DELETE_COUNTS INT = 0 ,@COUNT INT = 1; DECLARE @SummaryOfChanges table (Change VARCHAR(20));
IF OBJECT_ID('tempdb..#OS_CATGY_MAP') IS NOT NULL DROP TABLE #OS_CATGY_MAP;
CREATE TABLE #OS_CATGY_MAP( [OS_CATGY_KEY] [int] NOT NULL, [OS_CATGY_NM] [varchar](150) NULL, [OS_NM_QUALFR] [varchar](150) NULL, [ROW_STS_KEY] [int] NOT NULL, )
IF OBJECT_ID('tempdb..#OS_NM_QUAL') IS NOT NULL DROP TABLE #OS_NM_QUAL;
CREATE TABLE #OS_NM_QUAL( [OS_NM] [varchar](150) NULL, [OS_CATGY_KEY] [int] NOT NULL )
IF OBJECT_ID('tempdb..#OS_NM_INSERT') IS NOT NULL DROP TABLE #OS_NM_INSERT;
CREATE TABLE #OS_NM_INSERT( [OS_NM] [varchar](150) NULL, [OS_CATGY_KEY] [int] NOT NULL )
/*MAPPING TABLE BASED ON BUSINESS RULES*/ INSERT #OS_CATGY_MAP SELECT '1','Windows','windows','1' UNION ALL SELECT '2','Linux','linux','1' UNION ALL SELECT '2','Linux','Red Hat','1' UNION ALL SELECT '3','Unix','unix','1' UNION ALL SELECT '3','Unix','aix','1' UNION ALL SELECT '3','Unix','microsystems','1' UNION ALL SELECT '3','Unix','hp-ux','1' UNION ALL SELECT '3','Unix','sco opendesktop','1' UNION ALL SELECT '3','Unix','sco openserver','1' UNION ALL SELECT '3','Unix','sco release','1' UNION ALL SELECT '3','Unix','sco unix','1' UNION ALL SELECT '3','Unix','SCO','2' UNION ALL SELECT '4','Mac','mac os','1' UNION ALL SELECT '4','Mac','iOS','1' UNION ALL SELECT '5','VMWare','vmware','1' UNION ALL SELECT '6','MainFrame','ibm os','1' UNION ALL SELECT '7','Other',NULL,'1'
INSERT #OS_NM_QUAL ([OS_NM], [OS_CATGY_KEY]) SELECT DISTINCT LTRIM(RTRIM(A.OS_NM)) AS OS_NM ,B.OS_CATGY_KEY FROM @OS_NM A, #OS_CATGY_MAP B WHERE A.OS_NM LIKE '%' + B.OS_NM_QUALFR + '%' AND B.ROW_STS_KEY = 1
INSERT #OS_NM_INSERT ([OS_NM], [OS_CATGY_KEY]) SELECT OS_OTH.OS_NM ,ISNULL(OS_QUAL.OS_CATGY_KEY, (SELECT OS_CATGY_KEY FROM #OS_CATGY_MAP WHERE OS_CATGY_NM = 'Other')) AS OS_CATGY_KEY FROM (SELECT LTRIM(RTRIM(OS_NM)) AS OS_NM FROM @OS_NM) OS_OTH LEFT JOIN #OS_NM_QUAL OS_QUAL ON OS_OTH.OS_NM = OS_QUAL.OS_NM
SELECT @SRC_COUNTS = COUNT(*) FROM #OS_NM_INSERT PRINT N'Message: TOTAL ' + CAST(@SRC_COUNTS AS VARCHAR) + N' RECORDS FROM SOURCE.'
-- SET XACT_ABORT ON will cause the transaction to be uncommittable when the constraint violation occurs. SET XACT_ABORT ON;
BEGIN TRY
BEGIN TRANSACTION OS_MAP WITH MARK N'OS NAME INSERT DATA'
;WITH _target AS (
SELECT * FROM [dbo].[OS] ) , _source AS ( SELECT * FROM #OS_NM_INSERT )
MERGE INTO _target USING _source ON ( _target.OS_NM=_source.OS_NM --AND _target.OS_CATGY_KEY=_source.OS_CATGY_KEY ) WHEN NOT MATCHED THEN INSERT ( [OS_NM] ,[OS_CATGY_KEY] ,[CREAT_DT] ,[CREAT_USER_ID] ) VALUES ( _source.OS_NM ,_source.OS_CATGY_KEY ,GETDATE() ,'2' )
OUTPUT $ACTION INTO @SummaryOfChanges OPTION(RECOMPILE);
SELECT @INSERT_COUNTS = @@ROWCOUNT PRINT N'Message: Total ' + CAST(@INSERT_COUNTS AS VARCHAR) + N' VULN INSERTED.'
IF (XACT_STATE()) = 1 BEGIN PRINT N'The transaction is committable. Committing transaction.' COMMIT TRANSACTION OS_MAP; END;
END TRY
BEGIN CATCH
IF (XACT_STATE()) = -1 BEGIN PRINT N'The transaction is in an uncommittable state. Rolling back transaction.' ROLLBACK TRANSACTION OS_MAP END;
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState); END CATCH
END

/* --CREATE TYPE OS_NMs AS TABLE (OS_NM NVARCHAR(150));
DECLARE @OS_NM AS OS_NMs
INSERT @OS_NM(OS_NM) SELECT 'Cisco Aironet 1200 Series AP ' UNION ALL SELECT 'mac os ' UNION ALL SELECT 'TEST vmware TEST ' UNION ALL SELECT ' TEST mac os TEST'
--SELECT * FROM @OS_NM
EXEC [dbo].[MERGE_OS_MAP] @OS_NM */
CREATE PROCEDURE [dbo].[MERGE_OS_MAP] @OS_NM AS OS_NMs READONLY, @SRC_COUNTS INT OUTPUT, @INSERT_COUNTS INT OUTPUT AS

BEGIN
declare @mergeResultsTable table (MergeAction VARCHAR(20));
BEGIN TRY
SET NOCOUNT ON
MERGE DBO.REG_CMPLN AS T USING STG.REG_CMPLN_STG AS S ON S.REG_CMPLN_CD = T.REG_CMPLN_CD AND S.REG_CMPLN_VER = T.REG_CMPLN_VER WHEN NOT MATCHED BY TARGET
THEN INSERT (REG_CMPLN_CD, REG_CMPLN_VER, REG_CMPLN_NM, REG_CMPLN_DESC, REG_CMPLN_PUBL_DT, CREAT_DT, CREAT_USER_ID)
VALUES
(S.REG_CMPLN_CD, S.REG_CMPLN_VER, S.REG_CMPLN_NM, S.REG_CMPLN_DESC, S.REG_CMPLN_PUBL_DT, GETDATE(), 2)
WHEN MATCHED
THEN UPDATE SET T.REG_CMPLN_NM = S.REG_CMPLN_NM, T.REG_CMPLN_DESC = S.REG_CMPLN_DESC, T.REG_CMPLN_PUBL_DT = S.REG_CMPLN_PUBL_DT, T.UPDT_DT = GETDATE(), UPDT_USER_ID = 2
output $action into @mergeResultsTable;
select @ins_row_cnt = [INSERT], @upd_row_cnt = [UPDATE] from (select 'NOOP' MergeAction -- row for null merge into null union all select * from @mergeResultsTable) mergeResultsPlusEmptyRow pivot (count(MergeAction) for MergeAction in ([INSERT],[UPDATE],[DELETE])) as mergeResultsPivot;
END TRY
BEGIN CATCH
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
END CATCH
END

/****************************** ** Desc: To load REG_CMPLN table ** Auth: Prakash Selvaraj ** Date: 06/20/2016 ************************** ** Change History ************************** ** Date Author Description ** 06/20/2016 PS Inital Version ** 07/11/2016 PS Added Try-Catch to capture the errors **************************************/
/* EXEC [dbo].[MERGE_REG_CMPLN]
*/
CREATE PROCEDURE [dbo].[MERGE_REG_CMPLN] @ins_row_cnt int output, @upd_row_cnt int output AS

BEGIN
declare @mergeResultsTable table (MergeAction VARCHAR(20));
BEGIN TRY SET NOCOUNT ON
MERGE DBO.SECUR_CTL AS T USING ( SELECT STG.REG_CMPLN_CD, STG.REG_CMPLN_VER, STG.SECUR_CTL_CD, STG.SECUR_CTL_FAM_CD, STG.SECUR_CTL_FAM_NM, STG.SECUR_CTL_FAM_DESC, STG.SECUR_OBJ_CD, STG.SECUR_OBJ_NM, STG.SECUR_OBJ_DESC, STG.SECUR_CTL_NM, STG.SECUR_CTL_DESC, STG.CTL_TYP_NM, STG.TPC_TXT, STG.SECUR_CTL_IMPL, STG.RQR_FOR_CERT_IND FROM STG.SECUR_CTL_STG STG JOIN dbo.REG_CMPLN REG ON STG.REG_CMPLN_CD = REG.REG_CMPLN_CD AND STG.REG_CMPLN_VER = REG.REG_CMPLN_VER ) AS S ON S.REG_CMPLN_CD = T.REG_CMPLN_CD AND S.REG_CMPLN_VER = T.REG_CMPLN_VER AND S.SECUR_CTL_CD = T.SECUR_CTL_CD WHEN NOT MATCHED BY TARGET THEN INSERT ( REG_CMPLN_CD, REG_CMPLN_VER, SECUR_CTL_CD, SECUR_CTL_FAM_CD, SECUR_CTL_FAM_NM, SECUR_CTL_FAM_DESC, SECUR_OBJ_CD, SECUR_OBJ_NM, SECUR_OBJ_DESC, SECUR_CTL_NM, SECUR_CTL_DESC, CTL_TYP_NM, TPC_TXT, SECUR_CTL_IMPL, RQR_FOR_CERT_IND, CREAT_DT, CREAT_USER_ID ) VALUES ( S.REG_CMPLN_CD, S.REG_CMPLN_VER, S.SECUR_CTL_CD, S.SECUR_CTL_FAM_CD, S.SECUR_CTL_FAM_NM, S.SECUR_CTL_FAM_DESC, S.SECUR_OBJ_CD, S.SECUR_OBJ_NM, S.SECUR_OBJ_DESC, S.SECUR_CTL_NM, S.SECUR_CTL_DESC, S.CTL_TYP_NM, S.TPC_TXT, S.SECUR_CTL_IMPL, S.RQR_FOR_CERT_IND, GETDATE(), 2 ) WHEN MATCHED THEN UPDATE SET T.SECUR_CTL_FAM_CD = S.SECUR_CTL_FAM_CD, T.SECUR_CTL_FAM_NM = S.SECUR_CTL_FAM_NM, T.SECUR_CTL_FAM_DESC = S.SECUR_CTL_FAM_DESC, T.SECUR_OBJ_CD = S.SECUR_OBJ_CD, T.SECUR_OBJ_NM = S.SECUR_OBJ_NM, T.SECUR_OBJ_DESC = S.SECUR_OBJ_DESC, T.SECUR_CTL_NM = S.SECUR_CTL_NM, T.SECUR_CTL_DESC = S.SECUR_CTL_DESC, T.CTL_TYP_NM = S.CTL_TYP_NM, T.TPC_TXT = S.TPC_TXT, T.SECUR_CTL_IMPL = S.SECUR_CTL_IMPL, T.RQR_FOR_CERT_IND = S.RQR_FOR_CERT_IND, T.UPDT_DT = GETDATE(), T.UPDT_USER_ID = 2
output $action into @mergeResultsTable;
SELECT @ins_row_cnt = [INSERT], @upd_row_cnt = [UPDATE] FROM ( SELECT 'NOOP' MergeAction -- row for null merge into null UNION ALL SELECT * FROM @mergeResultsTable ) mergeResultsPlusEmptyRow
pivot ( count(MergeAction) for MergeAction in ([INSERT],[UPDATE],[DELETE]) ) as mergeResultsPivot;
END TRY
BEGIN CATCH
DECLARE @ErrorNumber INT = ERROR_NUMBER(); DECLARE @ErrorLine INT = ERROR_LINE(); DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE(); DECLARE @ErrorSeverity INT = ERROR_SEVERITY(); DECLARE @ErrorState INT = ERROR_STATE();
PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10)); PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));
RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
END CATCH
END

/****************************** ** Desc: To load SECUR_CTL table ** Auth: Prakash Selvaraj ** Date: 06/28/2016 ************************** ** Change History ************************** ** Date Author Description ** 06/20/2016 Prakash Selvaraj Inital Version ** 07/11/2016 Prakash Selvaraj Added Try-Catch to capture the errors **************************************/
/*
EXEC MERGE_SECUR_CTL ? OUTPUT , ? OUTPUT
*/
CREATE PROCEDURE [dbo].[MERGE_SECUR_CTL] @ins_row_cnt int output, @upd_row_cnt int output AS

/************************************* ** Desc: To load SECUR_CTL_MAP table ** Auth: Prakash Selvaraj ** Date: 06/28/2016 ************************** ** Change History ************************** ** Date Author Description ** 06/28/2016 Prakash Selvaraj InitIal Version ** 07/11/2016 Prakash Selvaraj Added Try-Catch to capture the errors **************************************/
/*
EXEC MERGE_SECUR_CTL_MAP ? OUTPUT , ? OUTPUT
*/
CREATE PROCEDURE [dbo].[MERGE_SECUR_CTL_MAP] @ins_row_cnt int output, @upd_row_cnt int output AS

BEGIN

	SET NOCOUNT ON;

	DECLARE
		@ROWS INT = 0
		,@TARGETROWS INT = 0			
		,@MESSAGE NVARCHAR(4000)
		,@DEBUG INT = 0
		,@COUNT INT = 1;
	DECLARE @SummaryOfChanges table (Change VARCHAR(20));


	IF OBJECT_ID('tempdb..#VULN_CATGY_MAP') IS NOT NULL
	DROP TABLE #VULN_CATGY_MAP;

	CREATE TABLE #VULN_CATGY_MAP(
		[VULN_NM] [varchar](150) NULL,
		[VULN_CATGY_CD] [varchar](10) NULL
	)


	/*MAPPING TABLE BASED ON BUSINESS RULES*/	 
	INSERT #VULN_CATGY_MAP
	SELECT DISTINCT LTRIM(RTRIM(VULN_NM)) AS [VULN_NM], NULL AS [VULN_CATGY_CD]
	FROM @VULN_NM

	SELECT @SRC_COUNTS = COUNT(*) FROM #VULN_CATGY_MAP
	PRINT N'Message: TOTAL ' + CAST(@SRC_COUNTS AS VARCHAR) + N' RECORDS FROM SOURCE.'

	-- SET XACT_ABORT ON will cause the transaction to be uncommittable when the constraint violation occurs. 
	SET XACT_ABORT ON;

	BEGIN TRY

		BEGIN TRANSACTION VULN_MAP WITH MARK N'OS NAME INSERT DATA'

		;WITH _target AS 
		(

			SELECT [VULN_NM]
			  ,[VULN_CATGY_CD]
			  ,[CREAT_DT]
			  ,[CREAT_USER_ID]
			FROM [dbo].[VULN]
		)
		, _source AS 
		(
			SELECT [VULN_NM], [VULN_CATGY_CD]
			FROM #VULN_CATGY_MAP
		)

		MERGE INTO _target
		USING _source ON (
			_target.VULN_NM=_source.VULN_NM
			--AND _target.VULN_CATGY_CD=_source.VULN_CATGY_CD
			)
		WHEN NOT MATCHED THEN INSERT (
			[VULN_NM]
			,[VULN_CATGY_CD]
			,[CREAT_DT]
			,[CREAT_USER_ID]
		)			
		VALUES (
			_source.[VULN_NM]
			,_source.[VULN_CATGY_CD]
			,GETDATE() 
			,'2' 
		)

		OUTPUT $ACTION INTO @SummaryOfChanges
		OPTION(RECOMPILE);

		SELECT @INSERT_COUNTS = @@ROWCOUNT
		PRINT N'Message: Total ' + CAST(@INSERT_COUNTS AS VARCHAR) + N' VULN INSERTED.'	

		IF (XACT_STATE()) = 1
		BEGIN
			PRINT N'The transaction is committable. Committing transaction.'
			COMMIT TRANSACTION VULN_MAP;
		END;

	END TRY


	BEGIN CATCH

		IF (XACT_STATE()) = -1
		BEGIN
			PRINT N'The transaction is in an uncommittable state. Rolling back transaction.'
			ROLLBACK TRANSACTION VULN_MAP
		END;

		DECLARE @ErrorNumber INT = ERROR_NUMBER();
		DECLARE @ErrorLine INT = ERROR_LINE();
		DECLARE @ErrorMessage NVARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();

		PRINT 'Actual error number: ' + CAST(@ErrorNumber AS VARCHAR(10));
		PRINT 'Actual line number: ' + CAST(@ErrorLine AS VARCHAR(10));

		RAISERROR(@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH

END

