INSERT @VULN_NM(VULN_NM) SELECT DISTINCT VULN_NM FROM [ABC5].[CLNT_VULN_INSTC] UNION ALL SELECT DISTINCT VULN_NM FROM [BAY4].[CLNT_VULN_INSTC] UNION ALL SELECT DISTINCT VULN_NM FROM MAX2.[CLNT_VULN_INSTC]
