	   @CLNT_ENGMT_USER_ASGN_KEY INTEGER,
	   @ROW_STS_KEY				 INTEGER,
	   @CLNT_ENGMT_CD			 VARCHAR(30),
	   @USER_ID					 INTEGER,
	   @SRV_LST_CD				 VARCHAR(150),
