CREATE PROCEDURE [dbo].[UPDOrgSchmaInd]  
( 
	@ORG_KEY				INTEGER,  
	@UPDT_USER_ID			INTEGER,
	@CREAT_ORG_SCHM_IND		CHAR  
)  
  
AS
